/* Needleman-Wunsch alignment program



 *



 * usage: progs file1 file2



 * where file1 and file2 are two dna or two protein sequences.



 * The sequences can be in upper- or lower-case an may contain ambiguity



 * Any lines beginning with ‘;’, ‘>’ or ‘<’ are ignored



 * Max file length is 65535 (limited by unsigned short x in the jmp struct)



 * A sequence with ⅓ or more of its elements ACGTU is assumed to be DNA



 * Output is in the file “align.out”



 *



 * The program may create a tmp file in /tmp to hold info about traceback.



 * Original version developed under BSD 4.3 on a vax 8650



 */



#include “nw.h”



#include “day.h”









static
_dbval[26] = {




1,14,2,13,0,0,4,11,0,0,12,0,3,15,0,0,0,5,6,8,8,7,9,0,10,0



};



static
_pbval[26] = {




1, 2|(1< <(‘D’-‘A’))|(1< <(‘N’-‘A’)), 4, 8, 16, 32, 64,




128, 256, 0×FFFFFFF, 1< <10, 1< <11, 1< <12, 1< <13, 1< <14,




1< <15, 1< <16, 1< <17, 1< <18, 1< <19, 1< <20, 1< <21, 1< <22,




1< <23, 1< <24, 1< <25|(1< <(‘E’-‘A’))|(1< <(‘Q’-‘A’))








};









main(ac, av)
main











int
ac;




char
*av[];








{










prog = av[0];




if(ac != 3) {










fprintf(stderr, “usage: %s file1 file2\n”, prog);




fprintf(stderr, “where file1 and file2 are two dna or two protein sequences.\n”);




fprintf(stderr, “The sequences can be in upper- or lower-case\n”);




fprintf(stderr, “Any lines beginning with ‘;’ or ‘<’ are ignored\n”);




fprintf(stderr, “Output is in the file \“align.out\”\n”);




exit(1);










}




namex[0] = av[1];




namex[1] = av[2];




seqx[0] = getseq(namex[0], &len0);




seqx[1] = getseq(namex[1], &len1);




xbm = (dna)? _dbval : _pbval;











endgaps = 0;
/* 1 to penalize endgaps */




ofile = “align.out”;
/* output file */











nw();
/* fill in the matrix, get the possible jmps */




readjmps();
/* get the actual jmps */




print();
/* print stats, alignment */




cleanup(0);
/* unlink any tmp files */








}



/* do the alignment, return best score: main()



 * dna: values in Fitch and Smith, PNAS, 80, 1382-1386, 1983



 * pro: PAM 250 values



 * When scores are equal, we prefer mismatches to any gap, prefer



 * a new gap to extending an ongoing gap, and prefer a gap in seqx



 * to a gap in seq y.



 */









nw()
nw



{












char
*px, *py;
/* seqs and ptrs */




int
*ndely, *dely;
/* keep track of dely */




int
ndelx, delx;
/* keep track of delx */




int
*tmp;
/* for swapping row0, row1 */




int
mis;
/* score for each type */




int
ins0, ins1;
/* insertion penalties */




register
id;
/* diagonal index */




register
ij;
/* jmp index */




register
*col0, *col1;
/* score for curr, last row */




register
xx, yy;
/* index into seqs */










dx = (struct diag *)g_calloc(“to get diags”, len0+len1+1, sizeof(struct diag));




ndely = (int *)g_calloc(“to get ndely”, len1+1, sizeof(int));




dely = (int *)g_calloc(“to get dely”, len1+1, sizeof(int));




col0 = (int *)g_calloc(“to get col0”, len1+1, sizeof(int));




col1 = (int *)g_calloc(“to get col1”, len1+1, sizeof(int));




ins0 = (dna)? DINS0 : PINS0;




ins1 = (dna)? DINS1 : PlNS1;




smax = −10000;




if (endgaps) {










for (col0[0] = dely[0] = −ins0, yy = 1; yy <= len1; yy++) {










col0[yy] = dely[yy] = col0[yy−1] − ins1;




ndely[yy] = yy;











}





col0[0] = 0;
/* Waterman Bull Math Biol 84 */










}




else










for (yy= 1; yy <= len1; yy++)










dely[yy] = −ins0;










/* fill in match matrix




 */




for (px = seqx[0], xx = 1; xx <= len0; px++, xx++) {










/* initialize first entry in col




 */




if (endgaps) {










if (xx == 1)










col1[0] = delx = −(ins0+ins1);










else










col1[0] = delx = col0[0]−ins1;










ndelx = xx;










}




else {










col1[0] = 0;




delx = −ins0;




ndelx = 0;










}










...nw










for (py = seqx[1], yy = 1; yy <= len1; py++, yy++) {










mis = col0[yy−1];




if (dna)










mis + = (xbm[*px−‘A’]&xbm[*py−‘A’])? DMAT : DMIS;










else










mis += _day[*px−‘A’][*py−‘A’];










/* update penalty for del in x seq;




 * favor new del over ongong del




 * ignore MAXGAP if weighting endgaps




 */




if (endgaps || ndely[yy] < MAXGAP) {










if (col0[yy] − ins0 >= dely[yy]) {










dely[yy] = col0[yy] − (ins0+ins1);




ndely[yy] = 1;










} else {










dely[yy] −= ins1;




ndely[yy]++;










}










} else {










if (col0[yy] − (ins0+ins1) >= dely[yy]) {










dely[yy] = col0[yy] − (ins0+ins1);




ndely[yy] = 1;










} else 










ndely[yy]++;










}




/* update penalty for del in y seq;




 * favor new del over ongong del




 */




if (endgaps || ndelx < MAXGAP) {










if(col1[yy−1] − ins0 >= delx) {










delx = col1[yy−1] − (ins0+ins1);




ndelx = 1;










} else {










delx −= ins1;




ndelx++;










}










} else {










if (col1[yy−1] − (ins0+ins1) >= delx) {










delx = col1[yy−1] − (ins0+ins1);




ndelx = 1;










} else










ndelx++;










}




/* pick the maximum score; we're favoring




 * mis over any del and delx over dely




 */










...nw










id = xx − yy + len1 − 1;




if (mis >= delx && mis >= dely[yy])










col1[yy] = mis;










else if (delx >= dely[yy]) {










col1[yy] = delx;




ij = dx[id].ijmp;




if (dx[id].jp.n[0] && (!dna || (ndelx >= MAXJMP




&& xx > dx[id].jp.x[ij]+MX) || mis > dx[id].score+DINS0)) {










dx[id].ijmp++;




if (++ij >= MAXJMP) {










writejmps(id);




ij = dx[id].ijmp = 0;




dx[id].offset = offset;




offset += sizeof(struct jmp) + sizeof(offset);










}










}




dx[id].jp.n[ij] = ndelx;




dx[id].jp.x[ij] = xx;




dx[id].score = delx;










}




else {










col1[yy] = dely[yy];




ij = dx[id].ijmp;








if (dx[id].jp.n[0] && (!dna || (ndely[yy] >= MAXJMP










&& xx > dx[id].jp.x[ij]+MX) || mis > dx[id].score+DINS0)) {










dx[id].ijmp++;




if (++ij >= MAXJMP) {










writejmps(id);




ij = dx[id].ijmp = 0;




dx[id].offset = offset;




offset += sizeof(struct jmp) + sizeof(offset);










}










}




dx[id].jp.n[ij] =−ndely[yy];




dx[id].jp.x[ij] = xx;




dx[id].score = dely[yy];










}




if (xx == len0 && yy < len1) {










/* last col




 */




if (endgaps)










col1[yy] −= ins0+ins1*(len1−yy);










if(col1[yy] > smax) {










smax = col1[yy];




dmax = id;










}










}










}




if (endgaps && xx < len0)










col1[yy−1] −= ins0+ins1*(len0−xx);










if (col1[yy−1] > smax) {










smax = col1[yy−1];




dmax = id;










}




tmp = col0; col0 = col1; col1 = tmp;




}




(void) free((char *)ndely);




(void) free((char *)dely);




(void) free((char *)col0);




(void) free((char *)col1);








}



/*



 *



 * print() -- only routine visible outside this module



 *



 * static:



 * getmat() -- trace back best path, count matches: print()



 * pr_align() -- print alignment of described in array p[]: print()



 * dumpblock() -- dump a block of lines with numbers, stars: pr_align()



 * nums() -- put out a number line: dumpblock()



 * putline() -- put out a line (name, [num], seq, [num]): dumpblock()



 * stars() - -put a line of stars: dumpblock()



 * stripname() -- strip any path and prefix from a seqname



 */



#include “nw.h”










#define SPC
 3




#define P_LINE
256
/* maximum output line */



#define P_SPC
 3
/* space between name or num and seq */










extern
_day[26][26];




int
olen;
/* set output line length */



FILE
*fx;
/* output file */









print()
print



{











int
lx, ly, firstgap, lastgap;  /* overlap */




if ((fx =
fopen(ofile, “w”)) == 0) {





fprintf(stderr, “%s: can't write %s\n”, prog, ofile);





cleanup(1);




}










fprintf(fx, “<first sequence: %s (length = %d)\n”, namex[0], len0);




fprintf(fx, “<second sequence: %s (length = %d)\n”, namex[1], len1);




olen = 60;




lx = len0;




ly = len1;




firstgap = lastgap = 0;











if (dmax < len1 − 1) {
/* leading gap in x */










pp[0].spc = firstgap = len1 − dmax − 1;




ly −= pp[0].spc;










}











else if (dmax > len1 − 1) {
/* leading gap in y */










pp[1].spc = firstgap = dmax − (len1 − 1);




lx −= pp[1].spc;










}











if (dmax0 < len0 − 1) {
/* trailing gap in x */










lastgap = len0 − dmax0 −1;




lx −= lastgap;










}











else if (dmax0 > len0 − 1) {
/* trailing gap in y */










lastgap = dmax0 − (len0 − 1);




ly −= lastgap;










}




getmat(lx, ly, firstgap, lastgap);




pr_align();








}



/*



 * trace back the best path, count matches



 */



static









getmat(lx, ly, firstgap, lastgap)
getmat












int
lx, ly;
/* “core” (minus endgaps) */




int
firstgap, lastgap;
/* leading trailing overlap */








{











int
nm, i0, i1, siz0, siz1;




char
outx[32];




double
pct;




register
n0, n1;




register char
*p0, *p1;










/* get total matches, score




 */




i0 = i1 = siz0 = siz1 = 0;




p0 = seqx[0] + pp[1].spc;




p1 = seqx[1] + pp[0].spc;




n0 = pp[1].spc + 1;




n1 = pp[0].spc + 1;




nm = 0;




while ( *p0 && *p1 ) {










if (siz0) {










p1++;




n1++;




siz0−−;










}




else if (siz1) {










p0++;




n0++;




siz1−−;










}




else {










if (xbm[*p0−‘A’]&xbm[*p1−‘A’])










nm++;










if (n0++ == pp[0].x[i0])










siz0 = pp[0].n[i0++];










if (nl++ == pp[1].x[i1])










siz1 = pp[1].n[il++];










p0++;




p1++;










}










}




/* pct homology:




 * if penalizing endgaps, base is the shorter seq




 * else, knock off overhangs and take shorter core




 */




if (endgaps)










lx = (len0 < len1)? len0 : len1;










else










lx = (lx < ly)? lx : ly;










pct = 100.*(double)nm/(double)lx;




fprintf(fx, “\n”);




fprintf(fx, “<%d match%s in an overlap of %d: %.2f percent similarity\n”,










nm, (nm == 1)? “” : “es”, lx, pct);











fprintf(fx, “<gaps in first sequence: %d”, gapx);
...getmat




if (gapx) {










(void) sprintf(outx, “(%d %s%s)”,










ngapx, (dna)? “base”: “residue”, (ngapx == 1)? “”:“s”);










fprintf(fx, “% s”, outx);










fprintf(fx, “, gaps in second sequence: %d”, gapy);




if (gapy) {










(void) sprintf(outx, “(%d %s%s)”,










ngapy, (dna)? “base”:“residue”, (ngapy == 1)? “”:“s”);










fprintf(fx, “%s”, outx);










}




if (dna)










fprintf(fx,




“\n<score: %d (match = %d, mismatch = %d, gap penalty = %d + %d per base)\n”,




smax, DMAT, DMIS, DINS0, DINS1);










else










fprintf(fx,




“\n<score: %d (Dayhoff PAM 250 matrix, gap penalty = %d + %d per residue)\n”,




smax, PINS0, PINS1);










if (endgaps)










fprintf(fx,




“<endgaps penalized. left endgap: %d %s%s, right endgap: %d %s%s\n”,




firstgap, (dna)? “base” : “residue”, (firstgap == 1)? “” : “s”,




lastgap, (dna)? “base” : “residue”, (lastgap == 1)? “” : “s”);










else










fprintf(fx, “<endgaps not penalized\n”);








}










