/*








 * print alignment of described in struct path pp[]



 */



static









pr_align()
pr_align



{












int
nn;
/* char count */




int
more;




register
i;










for (i = 0, lmax = 0; i < 2++) {










nn = stripname(namex[i]);











if (nn >
lmax)










lmax = nn;










nc[i] = 1;




ni[i] = 1;




siz[i] = ij[i] = 0;




ps[i] = seqx[i];




po[i] = out[i];










}











for (nn = nm = 0, more = 1; more;) {
...pr_align










for (i = more = 0; i < 2; i++) {










/*




 * do we have more of this sequence?




 */




if (!*ps[i])










continue;










more++;











if (pp[i].spc) {
/* leading space */










*po[i]++ = ‘ ’;




pp[i].spc−−;










}











else if (siz[i]) {
/* in a gap */










*po[i]++ = ‘−’;




siz[i]−−;










}











else {
/* we're putting a seq element










*/










*po[i] = *ps[i];




if (islower(*ps[i]))










*ps[i] = toupper(*ps[i]);










po[i]++;




ps[i]++;




/*




 * are we at next gap for this seq?




 */




if (ni[i] == pp[i].x[ij[i]]) {










/*




 * we need to merge all gaps




 * at this location




 */




siz[i] == pp[i].n[ij[i]++];




while (ni[i] == pp[i].x[ij[i]])










siz[i] += pp[i].n[ij[i]++];










}




ni[i]++;










}










}




if (++nn == olen || !more && nn) {










dumpblock();




for (i = 0; i < 2; i++)










po[i] = out[i];










nn = 0;










}










}








}



/*



 * dump a block of lines, including numbers, stars: pr_align()



 */



static









dumpblock()
dumpblock



{










register i;




for(i = 0; i < 2; i++)










*po[i]−− = ‘\0’;










...dumpblock










(void) putc(‘\n’, fx);




for (i = 0; i < 2; i++) {










if (*out[i] && (*out[i] != ‘ ’ || *(po[i]) != ‘ ’)) {










if (i == 0)










nums(i);










if (i == 0 && *out[1])










stars();










putline(i);




if (i == 0 && *out[1])










fprintf(fx, star);










if (i == 1)










nums(i);










}










}








}



* put out a number line: dumpblock()



 */



static









nums(ix)
nums











int  ix;
/* index in out[] holding seq line */








{











char
nline[P_LINE];




register
i, j;




register char
*pn, *px, *py;










for(pn = nline, i = 0; i < lmax+P_SPC; i++, pn++)










*pn = ‘ ’;










for (i = nc[ix], py = out[ix]; *py; py++, pn++) {










if (*py == ‘ ’ || *py == ‘−’);










*pn = ‘ ’;










else {










if (i%10 == 0 || (i == 1 && nc[ix] != 1)) {










j = (i < 0)? −i : i;




for (px = pn; j; j/= 10, px−−)










*px = j%10 + ‘0’;










if (i < 0)










*px = ‘−’;










}




else










*pn = ‘ ’;










i++;










}










}




*pn = ‘\0’;




nc[ix] = i;




for (pn = nline; *pn; pn++)










(void) putc(*pn, fx);










(void) putc(‘\n’, fx);








}



/*



 * put out a line (name, [num], seq. [num]): dumpblock()



 */



static









putline(ix)
putline










int   ix;








{










...putline











int
i;




register char
*px;










for (px = namex[ix], i = 0; *px && *px != ‘:’; px++, i++)










(void) putc(*px, fx);










for (;i < lmax+P_SPC; i++)










(void) putc(‘ ’, fx);










/* these count from 1:




 * ni[] is current element (from 1)




 * nc[] is number at start of current line




 */




for (px = out[ix]; *px; px++)










(void) putc(*px&0x7F, fx);










(void) putc(‘\n’, fx);








}



/*



 * put a line of stars (seqs always in out[0], out[1]): dumpblock()



 */



static









stars()
stars



{











int
i;




register char
*p0, *p1, cx, *px;











if
(!*out[0] || (*out[0] == ‘ ’ && *(p0[0]) == ‘ ’) ||





!*out[1] || (*out[1] == ‘ ’ && *(po[1]) == ‘ ’))










return;










px = star;




for (i = lmax+P_SPC; i; i−−)










*px++ = ‘ ’;










for (p0 = out[0], p1 = out[1]; *p0 && *p1; p0++, p1++) {










if (isalpha(*p0) && isalpha(*p1)) {










if (xbm[*p0−‘A’]&xbm[*p1−‘A’]) {










cx = ‘*’;




nm++;










}




else if (!dna && _day[*p0− ‘A’][*p1−‘A’] > 0)










cx = ‘.’;










else










cx = ‘ ’;










}




else










cx = ‘ ’;










*px++ = cx;










}




*px++ = ‘\n’;




*px = ‘\0’;








}



/*



 * strip path or prefix from pn, return len: pr_align()



 */



static









stripname(pn)
stripname












char
*pn;
/* file name (may be path) */








{











register char
*px, *py;










py = 0;




for (px = pn; *px; px++)










if (*px == ‘/’)










py = px + 1;










if (py)










(void) strcpy(pn, py);










return(strlen(pn));








}



/*



 * cleanup() -- cleanup any tmp file



 * getseq() -- read in seq, set dna, len, maxlen



 * g_calloc() -- calloc() with error checkin



 * readjmps() -- get the good jmps, from tmp file if necessary



 * writejmps() -- write a filled array of jmps to a tmp file: nw()



 */



#include “nw.h”



#include <sys/file.h>










char
*jname = “/tmp/homgXXXXXX”;
/* tmp file for jmps */



FILE
*fj;



int
cleanup();
/* cleanup tmp file */



long
lseek();



/*








 * remove any tmp file if we blow



 */









cleanup(i)
cleanup











int
i;








{










if (fj)










(void) unlink(jname);










exit(i);








}



/*



 * read, return ptr to seq, set dna, len, maxlen



 * skip lines starting with ‘;’, ‘<’, or ‘>’



 * seq in upper or lower case



 */



char *









getseq(file, len)
getseq












char
*file;
/* file name */




int
*len;
/* seq len */








{











char
line[1024], *pseq;




register char
*px, *py;




int
natgc, tlen;




FILE
*fp;










if ((fp = fopen(file, “r”)) == 0) {










fprintf(stderr, “%s: can't read %s\n”, prog, file);




exit(1);










}




tlen = natgc = 0;




while (fgets(line, 1024, fp)) {










if (*line == ‘;’ || *line == ‘<’ || *line == ‘>’)










continue;










for (px = line; *px != ‘\n’; px++)










if (isupper(*px) || islower(*px))










tlen++;










}




if ((pseq = malloc((unsigned)(tlen+6))) == 0) {










fprintf(stderr, “%s: malloc() failed to get %d bytes for %s\n”, prog, tlen+6, file);




exit(1);










}




pseq[0] = pseq[1] = pseq[2] = pseq[3] = ‘\0’;










...getseq










py = pseq + 4;




*len = tlen;




rewind(fp);




while (fgets(line, 1024, fp)) {










if (*line == ‘;’ || *line == ‘<’ || *line == ‘>’)










continue;










for (px = line; *px != ‘\n’; px++) {










if (isupper(*px))










*py++ = *px;










else if (islower(*px))










*py++ = toupper(*px);










if (index(“ATGCU”, *(py−1)))










natgc++;










}










}




*py++ = ‘\0’;




*py = ‘\0’;




(void) fclose(fp);




dna = natgc > (tlen/3);




return(pseq+4);








}



char *









g_calloc(msg, nx, sz)
g_calloc












char
*msg;
/* program, calling routine */




int
nx, sz;
/* number and size of elements */








{











char
*px, *calloc();










if ((px = calloc((unsigned)nx, (unsigned)sz)) == 0) {










if (*msg) {










fprintf(stderr, “%s: g_calloc() failed %s (n= %d, sz= %d)\n”, prog, msg, nx, sz);




exit(1);










}










}




return(px);








}



/*



 * get final jmps from dx[] or tmp file, set pp[], reset dmax: main()



 */









readjmps()
readjmps



{











int
fd = −1;




int
siz, i0, i1;










register i, j, xx;




if (fj) {










(void) fclose(fj);











if ((fd =
open(jname, O_RDONLY, 0)) < 0) {










fprintf(stderr, “%s: can't open() %s\n”, prog, jname);




cleanup(1);










}










}




for (i = i0 = i1 = 0, dmax0 = dmax, xx = len0; ;i++) {










while (1) {










for (j = dx[dmax].ijmp; j >= 0 && dx[dmax].jp.x[j] >= xx; j−−)










;










...readjmps










if (j < 0 && dx[dmax].offset && fj) {










(void) lseek(fd, dx[dmax].offset, 0);




(void) read(fd, (char *)&dx[dmax].jp, sizeof(struct jmp));




(void) read(fd, (char *)&dx[dmax].offset, sizeof(dx[dmax].offset));




dx[dmax].ijmp = MAXJMP−1;










}




else










break;










}




if (i >= JMPS) {










fprintf(stderr, “%s: too many gaps in alignment\n”, prog);




cleanup(1);










}




if (j >= 0) {










siz = dx[dmax].jp.n[j];




xx = dx[dmax].jp.x[j];




dmax += siz;











if (siz < 0) {
/* gap in second seq */










pp[1].n[il] = −siz;




xx += siz;




/* id = xx − yy + len1 − 1




 */




pp[1].x[il] = xx − dmax + len1 − 1;




gapy++;




ngapy −= siz;








/* ignore MAXGAP when doing endgaps */










siz = (−siz < MAXGAP || endgaps)? −siz : MAXGAP;




il++;










}











else if (siz > 0) {
/* gap in first seq */










pp[0] .n[i0] = siz;




pp[0] .x[i0] = xx;




gapx++;




ngapx += siz;








/* ignore MAXGAP when doing endgaps */










siz = (siz < MAXGAP || endgaps)? siz : MAXGAP;




i0++;










}










}




else










break;










}




/* reverse the order of jmps




 */




for (j = 0, i0−−; j < i0; j++, i0−−) {










i = pp[0].n[j]; pp[0].n[j] = pp[0].n[i0]; pp[0].n[i0] = i;




i = pp[0].x[j]; pp[0].x[j] = pp[0].x[i0]; pp[0].x[i0] = i;










}




for (j = 0, i1−−; j < i1; j++, i1−−) {










i = pp[1].n[j]; pp[1].n[j] = pp[1].n[i1]; pp[1].n[i1] = i;




i = pp[1].x[j]; pp[1].x[j] = pp[1].x[i1]; pp[1].x[i1] = i;










}




if (fd >= 0)










(void) close(fd);










if (fj) {










(void) unlink(jname);




fj = 0;




offset = 0;










}








}



/*



 * write a filled jmp struct offset of the prev one (if any): nw()



 */









writejmps(ix)
writejmps











int
ix;








{











char
*mktemp();










if (!fj) {










if (mktemp(jname) < 0) {










fprintf(stderr, “%s: can't mktemp() %s\n”, prog, jname);




cleanup(1);










}




if ((fj = fopen(jname, “w”)) == 0) {










fprintf(stderr, “%s: can't write %s\n”, prog, jname);




exit(1);










}










}




(void) fwrite((char *)&dx[ix].jp, sizeof(struct jmp), 1, fj);




(void) fwrite((char *)&dx[ix].offset, sizeof(dx[ix].offset), 1, fj);








}

