#include <stdio.h>





#include <ctype.h>



#define MAXJMP
16
/* max jumps in a diag */



#define MAXGAP
24
/* don't continue to penalize gaps larger





than this */



#define JMPS
1024
/* max jmps in an path */



#define MX
4
/* save if there's at least MX−1 bases





since last jmp */



#define DMAT
3
/* value of matching bases */



#define DMIS
0
/* penalty for mismatched bases */



#define DINS0
8
/* penalty for a gap */



#define DINS1
1
/* penalty per base */



#define PINS0
8
/* penalty for a gap */



#define PINS1
4
/* penalty per residue */



struct jmp {












short
n[MAXJMP];
/* size of jmp (neg for dely) */




unsigned short
x[MAXJMP];
/* base no. of jmp in seq x */










};

/* limits seq to 2{circumflex over ( )}16-1 */



struct diag {












int
score;
/* score at last jmp */




long
offset;
/* offset of prev block */




short
ijmp;
/* current jmp index */




struct jmp
jp;
/* list of jmps */








};



struct path {












int
spc;
/* number of leading spaces */




short
n[JMPS];
/* size of jmp (gap) */




int
x[JMPS];
/* loc of jmp (last elem before gap) */








};










