/* set by stars() */








/*



 * print alignment of described in struct path pp[]



 */



static









pr_align()
pr_align



{












int
nn;
/* char count */




int
more;




register
i;










for(i = 0, 1max = 0; i < 2; i++) {










nn = stripname(namex[i]);




if(nn > 1max)










1max = nn;










nc[i] = 1;




ni[i] = 1;




siz[i] = ij[i] = 0;




ps[i] = seqx[i];




po[i] = out[i];}










}










. . .pr_align










for (nn = nm = 0, more = 1; more;) {










for(i = more = 0; i < 2; i++) {










/*




 * do we have more of this sequence?




 */




if (!*ps[i])










continue;










more++;




if (pp[i].spc) { /* leading space */










 *po[i] ++ = ‘ ’;




